import React, { useState } from "react";
import { Link } from "react-router-dom";
import img from "../img/c1.png";
import axios from "axios";
import Back from "../components/common/back/Back";
import "./Reg.css";

const Reg = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [pic, setPic] = useState(""); // Initialize pic state

  // Default demo picture
  const defaultPic = "https://example.com/default-pic.jpg";

  async function submit(e) {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8000/Signup", {
        email,
        password,
        name,
        pic: pic || defaultPic, // Use the entered pic URL or the defaultPic if it's empty
      });

      if (response.data === "exist") {
        alert("User Already exists");
      } else if (response.data === "not exist") {
        // Save the email to local storage
        localStorage.setItem("userEmail", email);
        // Redirect to /header
        window.location.href = "/";
      }
    } catch (error) {
      alert("Wrong Details");
      console.error(error);
    }
  }
  return (
    <div className="p-10">
      <Back title="Login" />
      <div
        className="login-container bg-[#86e5dee4]
       justify-center items-center
       flex flex-col p-5"
      >
        <p className="login-title my-10 text-[30px]">SignUp</p>
        <form onSubmit={submit} className=" w-[100%]">
          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <label htmlFor="RegEmailAddress">Email Address</label>
            <input
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              placeholder="Email"
              id="RegEmailAddress"
              type="email"
              className="p-2"
            />
          </div>

          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <label htmlFor="regPassword">Password</label>
            <input
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              placeholder="Password"
              id="regPassword"
              type="password"
              className="p-2"
            />
          </div>

          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <label htmlFor="regName">Name</label>
            <input
              onChange={(e) => {
                setName(e.target.value);
              }}
              placeholder="Name"
              id="regName"
              className="p-2"
              type="text"
            />
          </div>

          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <label htmlFor="regPic">Pic URL</label>
            <input
              onChange={(e) => {
                setPic(e.target.value);
              }}
              placeholder="Pic URL"
              id="regPic"
              type="text"
              className="p-2"
            />
          </div>

          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <button type="submit w-[80%]">Submit</button>
          </div>
        </form>

        <div className="reg-links">
          <Link to="/login">or Login</Link>
        </div>
      </div>
    </div>
  );
};

export default Reg;
