import React from "react";
import { useLocation } from "react-router-dom";
import Image1 from "./IMAGES/1.png";
import Image2 from "./IMAGES/2.png";
import Image3 from "./IMAGES/3.png";
import Image4 from "./IMAGES/4.png";
import Image5 from "./IMAGES/5.png";

const Back = ({ title }) => {
  const location = useLocation();
  const locationName = location.pathname.split("/")[1];
  let backgroundImageUrl;

  if (locationName === "courses") {
    backgroundImageUrl = Image1;
  } else if (locationName === "about") {
    backgroundImageUrl = Image2;
  } else if (locationName === "test&notes") {
    backgroundImageUrl = Image3;
  } else if (locationName === "roadmap") {
    backgroundImageUrl = Image4;
  } else if (locationName === "profile") {
    backgroundImageUrl = Image5;
  } else {
    backgroundImageUrl =
      "https://srswebio.netlify.app/static/media/gitm8.b258a465e3808ddaec78.jpeg";
  }

  const backgroundStyle = {
    backgroundImage: `url(${backgroundImageUrl})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    backgroundAttachment: "fixed",
    backgroundPosition: "center",
    position: "absolute",
    top: 0,
    left: 0,
    zIndex: -1,
    width: "100%",
    height: "90vh",
    paddingTop: "30%",
    paddingRight: "50px",
    color: "#fff",
    textAlign: "center",
  };

  const headingStyle = {
    fontSize: "100px",
    fontWeight: 400,
  };

  const subheadingStyle = {
    fontWeight: 500,
    fontSize: "17px",
    textTransform: "uppercase",
  };

  return (
    <>
      <section style={backgroundStyle} className="back">
        <h2 style={subheadingStyle}>Home / {locationName}</h2>
        <h1 style={headingStyle}>{title}</h1>
      </section>
      <div className="margin"></div>
    </>
  );
};

export default Back;
