import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Head from "./Head";
import "./header.css";
import axios from "axios";

const Header = () => {
  const [click, setClick] = useState(false);
  const [subMenuVisible, setSubMenuVisible] = useState(false); // State for sub-menu visibility
  const [userProfile, setUserProfile] = useState(null);

  // Retrieve the email from local storage
  const userEmail = localStorage.getItem("userEmail");

  const handleResetClick = () => {
    // Clear the user's email from local storage
    localStorage.removeItem("userEmail");
    window.location.reload();
  };

  useEffect(() => {
    // Fetch all users for the UserProfile component
    axios
      .get("http://localhost:8000/users")
      .then((response) => {
        setUserProfile(response.data.find((user) => user.email === userEmail));
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });
  }, [userEmail]);

  const handleMainLiClick = () => {
    // Toggle the sub-menu visibility when the "More" (mainli) is clicked
    setSubMenuVisible(!subMenuVisible);
  };

  return (
    <>
      <Head />
      <header>
        <nav className="flexSB">
          <ul
            className={click ? "mobile-nav" : "flexSB "}
            onClick={() => setClick(false)}
          >
            {/* Your other menu items */}
            <li>
              <Link className="mainli" onClick={handleMainLiClick}>
                More
              </Link>
              <ul className={subMenuVisible ? "sub-menu" : "sub-menu hidden"}>
                <li>
                  <Link className="subli" to="/chatbot">
                    QueryHelpDesk
                  </Link>
                </li>
                <li>
                  <Link className="subli" to="/news">
                    News
                  </Link>
                </li>
                <li>
                  <Link className="subli" to="/profile">
                    Profile
                  </Link>
                </li>
                <li>
                  <Link className="subli" to="/contact">
                    Contact
                  </Link>
                </li>
                <li>
                  <Link className="subli" id="loginbtn" to="/signup">
                    Login
                  </Link>
                </li>
              </ul>
            </li>
            {/* Your other menu items */}
          </ul>
          <div className="start">{/* Your user profile section */}</div>
          <button className="toggle" onClick={() => setClick(!click)}>
            {click ? (
              <i className="fa fa-times"></i>
            ) : (
              <i className="fa fa-bars"></i>
            )}
          </button>
        </nav>
      </header>
    </>
  );
};

export default Header;
