import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import Back from "../components/common/back/Back";
import "./Login.css";
import Alert from "../components/Alert/Alert";
import OTPInput from "./OTPInput";

const Forgot = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const navigate = useNavigate();
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [otp, setOTP] = useState("");

  const generateOTP = () => {
    return Math.floor(100000 + Math.random() * 900000).toString();
  };
  const submit = async (e) => {
    e.preventDefault();

    try {
      // Generate OTP on the client side
      const otp = generateOTP();

      const response = await axios.post("http://localhost:8000/generate-otp", {
        email,
        otp, // Pass the generated OTP to the server
      });

      if (response.data === "OTP sent successfully") {
        setAlertMessage("OTP sent successfully!");
        handleShowAlert();
        // Continue with other logic as needed
      } else {
        setAlertMessage("Failed to send OTP");
        handleShowAlert();
      }
    } catch (error) {
      setAlertMessage("An error occurred");
      handleShowAlert();
      console.error(error);
    }
  };

  const handleShowAlert = () => {
    setShowAlert(true);
  };

  const handleCloseAlert = () => {
    setShowAlert(false);
  };

  return (
    <div className="p-10  drop-shadow-2xl shadow-2xl shadow-black">
      <Back title="Forgot Password" />

      <div className="login-container bg-[#86e5dee4] justify-center items-center flex flex-col p-5">
        <p className="login-title my-10 text-[30px]">Forgot</p>
        <form onSubmit={submit}>
          <div className="form-group flex flex-col">
            <label htmlFor="LoggingEmailAddress">Email Address</label>
            <input
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              placeholder="Email"
              id="LoggingEmailAddress"
              className="login-input"
              type="email"
            />
          </div>

          <div className="form-group">
            <label htmlFor="loggingPassword">Password</label>
            <input
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              placeholder="Password"
              id="loggingPassword"
              className="login-input"
              type="password"
            />
          </div>
          <div className="form-group">
            <label htmlFor="ConfirmPassword">Confirm Password</label>
            <input
              onChange={(e) => {
                setConfirmPassword(e.target.value);
              }}
              placeholder="Confirm Password"
              id="ConfirmPassword"
              className="login-input"
              type="password"
            />
          </div>

          <div className="submit-button">
            <button type="submit" className="login-button">
              Submit
            </button>
          </div>
        </form>
        <div className="login-links ">
          <Link to="/login" className="login-link mr-10">
            Back to Login
          </Link>
        </div>
      </div>

      {showAlert && <Alert message={alertMessage} onClose={handleCloseAlert} />}
    </div>
  );
};

export default Forgot;
