import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import img from "../img/c1.png";
import axios from "axios";
import Back from "../components/common/back/Back";
import "./Reg.css";
import pic from "./logo.png";

const Reg = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [department, setDepartment] = useState("");
  const [name, setName] = useState("");
  const [pic, setPic] = useState(""); // Initialize pic state
  const [useDemoPic, setUseDemoPic] = useState(false);

  // Default demo picture
  const defaultPic =
    "https://image.lexica.art/full_webp/fc4d6aac-7a70-4e63-ada1-eb84f69e2ae6";

  async function submit(e) {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8000/Signup", {
        email,
        password,
        name,
        department,
        pic: pic || defaultPic, // Use the entered pic URL or the defaultPic if it's empty
      });

      if (response.data === "exist") {
        alert("User Already exists");
      } else if (response.data === "not exist") {
        // Save the email to local storage
        localStorage.setItem("userEmail", email);

        // Redirect to /header
        window.location.href = "/";
      }
    } catch (error) {
      alert("Wrong Details");
      console.error(error);
    }
  }

  // ---------google ---------------------

  function handleCallbackResponse(response) {
    if (response && response.credential) {
      console.log("encoded jwt id: " + response.credential);
    } else {
      console.error("Invalid or missing response:", response);
    }
  }

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://accounts.google.com/gsi/client";
    script.async = true;
    script.defer = true;
    document.head.appendChild(script);

    script.onload = () => {
      /* global google */
      if (google && google.account && google.account.id) {
        google.account.id.initialize({
          client_id: "your_client_id",
          callback: handleCallbackResponse,
        });

        google.account.id.renderButton(document.getElementById("signInDiv"), {
          theme: "outline",
          size: "large",
        });
      } else {
        console.error("Google API library or google.account.id is undefined.");
      }
    };
  }, []);

  return (
    <div className="p-10">
      <Back title="Login" />

      <div id="signInDiv"></div>
    </div>
  );
};

export default Reg;
