import React, { useState, useEffect, useRef } from "react";
import { AiFillRobot, AiOutlineSlack } from "react-icons/ai";
import Back from "../common/back/Back";

import { useLocation } from "react-router-dom";
import "./Chatbot.css";

const Chatbot = () => {
  const handleMainClick = () => {
    setExtraVisible(!isExtraVisible);
  };

  const [isExtraVisible1, setExtraVisible1] = useState(false);

  const handleMainClick1 = () => {
    setExtraVisible1(!isExtraVisible1);
  };

  const [isExtraVisible2, setExtraVisible2] = useState(false);

  const handleMainClick2 = () => {
    setExtraVisible2(!isExtraVisible2);
  };

  const [isExtraVisible3, setExtraVisible3] = useState(false);

  const handleMainClick3 = () => {
    setExtraVisible3(!isExtraVisible3);
  };

  const [isExtraVisible4, setExtraVisible4] = useState(false);

  const handleMainClick4 = () => {
    setExtraVisible4(!isExtraVisible4);
  };

  const [isExtraVisible5, setExtraVisible5] = useState(false);

  const handleMainClick5 = () => {
    setExtraVisible5(!isExtraVisible5);
  };

  const [navmain, navhead] = useState(false);
  const nav = () => {
    navhead((show) => !show);
  };

  const [isExtraVisible, setExtraVisible] = useState(false);

  const chatboxRef = useRef(null);
  const [userMessage, setUserMessage] = useState("");
  const [chatMessages, setChatMessages] = useState([]);
  const [rotationAngle, setRotationAngle] = useState(0); // Rotation state

  const API_KEY = "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";

  const createChatLi = (message, className) => {
    const chatLi = (
      <li className={`chat ${className}`} key={chatMessages.length}>
        {className === "outgoing" ? (
          <p>{message}</p>
        ) : (
          <>
            <span className="text-[30px] p-2">
              <AiFillRobot />
            </span>
            <p>{message}</p>
          </>
        )}
      </li>
    );
    return chatLi;
  };

  const generateResponse = async () => {
    const API_URL = "https://api.openai.com/v1/chat/completions";

    try {
      const requestOptions = {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${API_KEY}`,
        },
        body: JSON.stringify({
          model: "gpt-3.5-turbo",
          messages: [{ role: "user", content: userMessage }],
        }),
      };

      const response = await fetch(API_URL, requestOptions);
      const data = await response.json();

      const responseMessage = data.choices[0].message.content.trim();

      // Check if the response message is already in chatMessages
      const isMessageRepeated = chatMessages.includes(responseMessage);

      if (!isMessageRepeated) {
        setChatMessages([...chatMessages, userMessage, responseMessage]);
      }

      setUserMessage("");
    } catch (error) {
      console.error("Error generating response:", error);
    }
  };

  const handleChat = () => {
    if (userMessage.trim() === "") return;

    setChatMessages([...chatMessages, userMessage]);
    setUserMessage("");

    setTimeout(() => {
      setChatMessages((prevMessages) => [...prevMessages, "Thinking..."]);
      chatboxRef.current.scrollTop = chatboxRef.current.scrollHeight;

      generateResponse();
    }, 600);
  };

  const handleChange = (event) => {
    setUserMessage(event.target.value);
  };

  const handleSendMessage = () => {
    handleChat();
  };

  const handleIconClick = () => {
    // Rotate the icon by 180 degrees on each click
    setRotationAngle((prevAngle) => prevAngle + 180);
  };

  useEffect(() => {
    chatboxRef.current.scrollTop = chatboxRef.current.scrollHeight;
  }, [chatMessages]);

  return (
    <div>
      {/* <div className="chatCbotbg">
        <Back title="QueryHelpDesk" />

        <div
          className=" justify-center
     items-center flex p-5
      flex-col
      "
        >
          <div className=" w-[90%] md:w-[60%] bg-[#1eb2a6]     rounded-3xl">
            <header className="text md:text-[30px] text-[25px] rounded-t-3xl  text-[white]  font-bold">
              <h2>
                Query<span className="text-[black]">Help</span>Desk
              </h2>
            </header>

            <ul
              className="chatbox bg-[#ffffffa8] h-80 p-5 overflow-y-auto"
              ref={chatboxRef}
            >
              {chatMessages.map((message, index) => {
                if (message === "Thinking...") {
                  return createChatLi(message, "thinking");
                }
                return createChatLi(
                  message,
                  index % 2 === 0 ? "outgoing" : "incoming"
                );
              })}
            </ul>

            <div className=" flex items-center p-2 bg-[#61c48257] rounded-b-2xl justify-around">
              <textarea
                className="w-[90%]  p-1    rounded-2xl justify-center items-center"
                placeholder="Enter a message..."
                spellCheck="false"
                required
                value={userMessage}
                onChange={handleChange}
              ></textarea>
              <span
                id="send-btn"
                className="material-symbols-rounded"
                onClick={handleIconClick}
              >
                <AiOutlineSlack
                  onClick={handleSendMessage}
                  className={`text-[30px] transform rotate-${rotationAngle}`}
                />
              </span>
            </div>
          </div>
        </div>
      </div> */}

      <div>
        <div className="text-[20px] md:text-[30px] items-center">
          <div
            onClick={handleMainClick}
            className="justify-between m-2 p-4 rounded-xl bg-[#4b4646] flex"
          >
            <div className="">What is Netflix?</div>
            <div className="main duration-300 ease-in-out">⬇️</div>
          </div>
          {isExtraVisible && (
            <div className="extra text-[20px] bg-[#201d1d] rounded-lg p-3  duration-300 ease-in-out">
              Netflix is a streaming service that offers a wide variety of
              award-winning TV shows, movies, anime, documentaries and more – on
              thousands of internet-connected devices. You can watch as much as
              you want, whenever you want, without a single ad – all for one low
              monthly price. There's always something new to discover, and new
              TV shows and movies are added every week!
            </div>
          )}

          <div
            onClick={handleMainClick1}
            className=" m-2 justify-between p-4 rounded-xl bg-[#4b4646] flex"
          >
            <div className="">How much does Netflix cost?</div>
            <div className="main duration-300 ease-in-out">⬇️</div>
          </div>
          {isExtraVisible1 && (
            <div className="extra text-[20px] bg-[#201d1d] rounded-lg p-3  duration-300 ease-in-out">
              Watch Netflix on your smartphone, tablet, Smart TV, laptop, or
              streaming device, all for one fixed monthly fee. Plans range from
              ₹ 149 to ₹ 649 a month. No extra costs, no contracts.
            </div>
          )}

          <div
            onClick={handleMainClick2}
            className=" m-2 justify-between p-4 rounded-xl bg-[#4b4646] flex"
          >
            <div className="">Where can I watch?</div>
            <div className="main duration-300 ease-in-out">⬇️</div>
          </div>
          {isExtraVisible2 && (
            <div className="extra text-[20px] bg-[#201d1d] rounded-lg p-3  duration-300 ease-in-out">
              Watch anywhere, anytime. Sign in with your Netflix account to
              watch instantly on the web at netflix.com from your personal
              computer or on any internet-connected device that offers the
              Netflix app, including smart TVs, smartphones, tablets, streaming
              media players and game consoles. You can also download your
              favourite shows with the iOS, Android, or Windows 10 app. Use
              downloads to watch while you're on the go and without an internet
              connection. Take Netflix with you anywhere.
            </div>
          )}

          <div
            onClick={handleMainClick3}
            className=" m-2 justify-between p-4 rounded-xl bg-[#4b4646] flex"
          >
            <div className="">How do I cancel?</div>
            <div
              className="main duration-300 ease-in-out"
              onClick={handleMainClick3}
            >
              ⬇️
            </div>
          </div>
          {isExtraVisible3 && (
            <div className="extra text-[20px] bg-[#201d1d] rounded-lg p-3  duration-300 ease-in-out">
              Netflix is flexible. There are no annoying contracts and no
              commitments. You can easily cancel your account online in two
              clicks. There are no cancellation fees – start or stop your
              account anytime.
            </div>
          )}

          <div
            onClick={handleMainClick4}
            className="m-2 justify-between p-4 rounded-xl bg-[#4b4646] flex"
          >
            <div className="">What can I watch on Netflix?</div>
            <div className="main duration-300 ease-in-out">⬇️</div>
          </div>
          {isExtraVisible4 && (
            <div className="extra text-[20px] bg-[#201d1d] rounded-lg p-3  duration-300 ease-in-out">
              Netflix has an extensive library of feature films, documentaries,
              TV shows, anime, award-winning Netflix originals, and more. Watch
              as much as you want, anytime you want.
            </div>
          )}

          <div
            onClick={handleMainClick5}
            className="m-2 justify-between p-4 rounded-xl bg-[#4b4646] flex"
          >
            <div className="">Is Netflix good for kids?</div>
            <div className="main duration-300 ease-in-out">⬇️</div>
          </div>
          {isExtraVisible5 && (
            <div className="extra text-[20px] bg-[#201d1d] rounded-lg p-3  duration-300 ease-in-out">
              The Netflix Kids experience is included in your membership to give
              parents control while kids enjoy family-friendly TV shows and
              films in their own space. Kids profiles come with PIN-protected
              parental controls that let you restrict the maturity rating of
              content kids can watch and block specific titles you don’t want
              kids to see.
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
