import React, { useState } from "react";
import img from "../img/c1.png";
import { Link } from "react-router-dom";
import axios from "axios";

import Back from "../components/common/back/Back";

import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  async function submit(e) {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8000/", {
        email,
        password,
      });

      if (response.data === "exist") {
        // Redirect to /header with email state
        window.location.href = `/?id=${email}`;
      } else if (response.data === "not exist") {
        alert("User is not signed up");
        window.location.href = `/signup?id=${email}`;
      }
    } catch (error) {
      alert("Wrong Details");
      console.error(error);
    }
  }

  return (
    <div className="p-10  drop-shadow-2xl
     shadow-2xl shadow-black">
      <Back title="SignUp" />

      <div className="login-container bg-[#86e5dee4]
       justify-center items-center
       flex flex-col p-5">

        <p className="login-title my-10 text-[30px]">Log In</p>
        <form onSubmit={submit}>
          <div className="form-group flex flex-col ">
            <label htmlFor="LoggingEmailAddress">Email Address</label>
            <input
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              placeholder="Email"
              id="LoggingEmailAddress"
              className="login-input"
              type="email"
            />
          </div>

          <div className="form-group">
            <label htmlFor="loggingPassword">Password</label>
            <input
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              placeholder="Password"
              id="loggingPassword"
              className="login-input"
              type="password"
            />
          </div>

          <div className="submit-button">
            <button type="submit" className="login-button">
              Submit
            </button>
          </div>
        </form>
        <div className="login-links">
          <Link to="/Signup" className="login-link">
            or sign up
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
