import React, { useState, useEffect } from "react";
import axios from "axios";

const BlogCard = () => {
  const [loading, setLoading] = useState(true); // Define 'loading' state
  const [blogData, setBlogData] = useState([]); // Define 'blogData' state

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://api.markets.sh/api/v1/u/portfolios",
          {
            params: { api_token: "71e449bb1224b1cbde569dbb1a9ebb69" },
          }
        );

        setBlogData(response.data); // Update 'blogData' state
        setLoading(false); // Set 'loading' to false once data is received
      } catch (error) {
        console.error("Error fetching data:", error.message);
        setLoading(false); // Set 'loading' to false in case of an error
      }
    };

    fetchData();
  }, []); // Empty dependency array to ensure useEffect runs only once

  return (
    <>
      {loading ? (
        <p className="text-black">Loading...</p>
      ) : blogData.length > 0 ? (
        blogData.map((val) => (
          <div key={val.id} className="items shadow">
            <div className="img">
              <img src={val.cover} alt="" />
            </div>
            <div className="text">
              <div className="admin flexSB">
                <span>
                  <i className="fa fa-user"></i>
                  <label htmlFor="">{val.type}</label>
                </span>
                <span>
                  <i className="fa fa-calendar-alt"></i>
                  <label htmlFor="">{val.date}</label>
                </span>
                <span>
                  <i className="fa fa-comments"></i>
                  <label htmlFor="">{val.com}</label>
                </span>
              </div>
              <h1>{val.title}</h1>
              <p>{val.desc}</p>
            </div>
          </div>
        ))
      ) : (
        <p className="text-black">No data available.</p>
      )}
    </>
  );
};

export default BlogCard;
