import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import img from "../img/c1.png";
import axios from "axios";
import Back from "../components/common/back/Back";
import "./Reg.css";
import pic from "./logo.png";

const Reg = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [department, setDepartment] = useState("");
  const [name, setName] = useState("");
  const [pic, setPic] = useState(""); // Initialize pic state
  const [useDemoPic, setUseDemoPic] = useState(false);

  // Default demo picture
  const defaultPic =
    "https://image.lexica.art/full_webp/fc4d6aac-7a70-4e63-ada1-eb84f69e2ae6";

  async function submit(e) {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8000/Signup", {
        email,
        password,
        name,
        department,
        pic: pic || defaultPic, // Use the entered pic URL or the defaultPic if it's empty
      });

      if (response.data === "exist") {
        alert("User Already exists");
      } else if (response.data === "not exist") {
        // Save the email to local storage
        localStorage.setItem("userEmail", email);

        // Redirect to /header
        window.location.href = "/";
      }
    } catch (error) {
      alert("Wrong Details");
      console.error(error);
    }
  }

  // ---------google ---------------------

  function handleCallbackResponse(response) {
    if (response && response.credential) {
      console.log("encoded jwt id: " + response.credential);
    } else {
      console.error("Invalid or missing response:", response);
    }

    console.log(response);
    console.log(response.credential);
  }

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://apis.google.com/js/platform.js"; // Corrected script source URL
    script.defer = true;

    const onScriptLoad = () => {
      console.log("Google API script loaded");

      if (window.gapi && window.gapi.signin2) {
        console.log("Initializing Google Sign-In");

        const clientId =
          "474306748991-ljgkaqr2ao7s5e1clg08ul019d6m4gm0.apps.googleusercontent.com";

        window.gapi.signin2.render("signInDiv", {
          scope: "profile email",
          width: 200,
          height: 50,
          longtitle: true,
          theme: "light",
          onsuccess: handleCallbackResponse,
          onfailure: (error) => console.error("Google Sign-In failed:", error),
          client_id: clientId,
        });
      } else {
        console.error(
          "Google API library or gapi.signin2 is undefined. Retrying in 1 second..."
        );

        // Retry after 1 second
        setTimeout(onScriptLoad, 1000);
      }
    };

    script.addEventListener("load", onScriptLoad);
    script.addEventListener("error", () => {
      console.error("Error loading Google API script");
    });

    document.head.appendChild(script);

    return () => {
      // Cleanup script and event listeners on component unmount
      script.removeEventListener("load", onScriptLoad);
      document.head.removeChild(script);
    };
  }, []);

  return (
    <div className="p-10">
      <Back title="Login" />
      <div
        className="login-container bg-[#86e5dee4]
       justify-center items-center
       flex flex-col p-5"
      >
        <p className="login-title my-10 text-[30px]">SignUp</p>
        <form onSubmit={submit} className=" w-[100%]">
          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <label htmlFor="RegEmailAddress">Email Address</label>
            <input
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              placeholder="Email"
              id="RegEmailAddress"
              type="email"
              className="p-2"
            />
          </div>

          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <label htmlFor="regPassword">Password</label>
            <input
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              placeholder="Password"
              id="regPassword"
              type="password"
              className="p-2"
            />
          </div>

          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <label htmlFor="regName">Name</label>
            <input
              onChange={(e) => {
                setName(e.target.value);
              }}
              placeholder="Name"
              id="regName"
              className="p-2"
              type="text"
            />
          </div>

          <div className="reg-form-group flex flex-col p-4">
            <label htmlFor="regDepartment">Department</label>
            <select
              onChange={(e) => {
                setDepartment(e.target.value);
              }}
              id="regDepartment"
              className="p-2"
            >
              <option value="" disabled selected>
                Select Department
              </option>
              <option value="csa">CSA</option>
              <option value="cse">CSE</option>
            </select>
          </div>

          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <label htmlFor="regPic">Pic URL</label>
            <input
              onChange={(e) => {
                setPic(e.target.value);
              }}
              placeholder="Profile Pic Url "
              id="regPic"
              type="text"
              className="p-2"
            />
            <div className="  justify-center items-center flex ">
              <p>Or</p>
            </div>
            <p className="demo-pic-text  justify-center items-center flex">
              Use default demo picture:
              <input
                type="checkbox"
                className="demo-pic-checkbox ml-2 "
                checked={useDemoPic}
                onChange={() => setUseDemoPic(!useDemoPic)}
              />
            </p>
          </div>

          <div
            className="reg-form-group flex flex-col p-4
        "
          >
            <button type="submit w-[80%]">Submit</button>
          </div>
        </form>

        <div className="reg-links">
          <Link className=" text" to="/login">
            or Already have Account ?{" "}
            <span className=" text-[blue]">Login</span>
          </Link>
        </div>
      </div>
      <div id="signInDiv"></div>
    </div>
  );
};

export default Reg;
