import React from "react";
import { useLocation } from "react-router-dom";

const Back = ({ title }) => {
  const location = useLocation();
  const locationName = location.pathname.split("/")[1];
  let backgroundImageUrl;

  // Conditionally set background image based on location pathname
  if (locationName === "courses") {
    backgroundImageUrl =
      "https://imgs.search.brave.com/d_nuIj__558vY7RNgJgFQhUTnrzZksZSJkd1NQuIABc/rs:fit:860:0:0/g:ce/aHR0cHM6Ly93d3cu/d2FsbHBhcGVyc3Rv/Z28uY29tL2ltYWdl/cy9tZWRpYS9mZWF0/dXJlZC1tYW51ZmFj/dHVyZXItMS5qcGc";
  } else {
    backgroundImageUrl =
      "https://imgs.search.brave.com/Y1bNpePnJhi8Fb7IDsG7jt9y9thygwvvIkU8igxHd5k/rs:fit:860:0:0/g:ce/aHR0cHM6Ly9pLnBp/bmltZy5jb20vb3Jp/Z2luYWxzLzNhLzk0/L2M5LzNhOTRjOTM3/YTg3MmFlMTc1NGZj/ODJkZWY1ZTM1N2Mx/LmpwZw";
  }

  const backgroundStyle = {
    backgroundImage: `url(${backgroundImageUrl})`,
  };

  return (
    <>
      <section style={backgroundStyle}>
        <h2>Home / {locationName}</h2>
        <h1>{title}</h1>
      </section>
      <div className="margin"></div>
    </>
  );
};

export default Back;
