import React, { useEffect, useState } from "react";
import axios from "axios";

const CoursesCard = () => {
  const [productData, setProductData] = useState([]);

  useEffect(() => {
    const fetchProductData = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/products");
        setProductData(response.data);
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    };

    fetchProductData();
  }, []);

  return (
    <div className="flex flex-wrap justify-center">
      {productData.map((course, index) => (
        <div
          key={index}
          className="max-w-sm rounded overflow-hidden shadow-lg m-4 transform hover:translate-y-1 transition duration-300"
        >
          <img
            className="w-full h-48 object-cover"
            src={course.imageLink}
            alt={course.imageName}
          />
          <div className="px-6 py-4">
            <div className="font-bold text-xl mb-2">{course.imageName}</div>
            <p className="text-gray-700 text-base">
              Stars: {course.starsCount}
            </p>
            <p className="text-gray-700 text-base">Hours: {course.hours}</p>
            <div className="flex justify-between mt-4">
              <a
                href={course.link1}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Link 1
              </a>
              <a
                href={course.link2}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Link 2
              </a>
              <a
                href={course.link3}
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Link 3
              </a>
            </div>
          </div>
          <div className="px-6 py-4">
            <a
              href={course.finalLink}
              className="text-blue-500 hover:text-blue-700"
            >
              Final Link
            </a>
          </div>
        </div>
      ))}
    </div>
  );
};

export default CoursesCard;
