import React, { useState, useEffect } from "react";
import axios from "axios";

const BlogCard = () => {
  const [blogData, setBlogData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch data from the API endpoint
        const response = await axios.get(
          "https://api.markets.sh/api/v1/u/portfolios?api_token=71e449bb1224b1cbde569dbb1a9ebb69"
        );

        // Log the API response to the console
        console.log("API Response:", response.data);

        // Check if the response data is an array
        if (Array.isArray(response.data)) {
          // Set the fetched data to the state
          setBlogData(response.data);

          // Set loading to false once data is received
          setLoading(false);
        } else {
          console.error("Invalid data format received from the API");
          // Set loading to false in case of invalid data format
          setLoading(false);
        }
      } catch (error) {
        console.error("Error fetching blog data:", error);
        // Set loading to false in case of an error
        setLoading(false);
      }
    };

    // Call the fetchData function
    fetchData();
  }, []);

  return (
    <>
      {loading ? (
        <p className="text-black">Loading...</p>
      ) : blogData.length > 0 ? (
        blogData.map((val) => (
          <div key={val.id} className="items shadow">
            <div key={val.id} className="items shadow">
              <div className="img">
                <img src={val.cover} alt="" />
              </div>
              <div className="text">
                <div className="admin flexSB">
                  <span>
                    <i className="fa fa-user"></i>
                    <label htmlFor="">{val.type}</label>
                  </span>
                  <span>
                    <i className="fa fa-calendar-alt"></i>
                    <label htmlFor="">{val.date}</label>
                  </span>
                  <span>
                    <i className="fa fa-comments"></i>
                    <label htmlFor="">{val.com}</label>
                  </span>
                </div>
                <h1>{val.title}</h1>
                <p>{val.desc}</p>
              </div>
            </div>
          </div>
        ))
      ) : (
        <p className="text-black">No data available.</p>
      )}
    </>
  );
};

export default BlogCard;
