import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
  const [userProfile, setUserProfile] = useState(null);

  // Function to fetch user profile data
  const fetchUserProfile = () => {
    axios
      .get("http://localhost:8000/users/current-user")
      .then((response) => {
        if (response.data.success) {
          setUserProfile(response.data.user);
        } else {
          // Handle the case where the user's profile is not found
          console.error("User profile not found");
        }
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });
  };

  useEffect(() => {
    // Call the function to fetch user profile data when the component loads
    fetchUserProfile();
  }, []);

  return (
    <div>
      {userProfile && (
        <div>
          <img src={userProfile.pic} alt="User Profile" />
          <h2>{userProfile.name}</h2>
          <p>Email: {userProfile.email}</p>
        </div>
      )}
    </div>
  );
};

export default Profile;
