import React, { useState } from "react";
import ReactGlobe from "react-globe";
import { Link } from "react-router-dom";
import Heading from "../common/heading/Heading";
import "./about.css";
import { homeAbout } from "../../dummydata";
import Awrapper from "./Awrapper";

const AboutCard = () => {
  const markers = [
    {
      id: "marker1",
      city: "Singapore",
      color: "red",
      coordinates: [1.3521, 103.8198],
      value: 50,
    },
    {
      id: "marker2",
      city: "New York",
      color: "blue",
      coordinates: [40.73061, -73.935242],
      value: 25,
    },
    {
      id: "marker3",
      city: "San Francisco",
      color: "orange",
      coordinates: [37.773972, -122.431297],
      value: 35,
    },
    {
      id: "marker4",
      city: "Beijing",
      color: "gold",
      coordinates: [39.9042, 116.4074],
      value: 135,
    },
    {
      id: "marker5",
      city: "London",
      color: "green",
      coordinates: [51.5074, 0.1278],
      value: 80,
    },
    {
      id: "marker6",
      city: "Los Angeles",
      color: "gold",
      coordinates: [29.7604, -95.3698],
      value: 54,
    },
  ];

  const options = {
    ambientLightColor: "red",
    cameraRotateSpeed: 0.5,
    focusAnimationDuration: 2000,
    focusEasingFunction: ["Linear", "None"],
    pointLightColor: "gold",
    pointLightIntensity: 1.5,
    globeGlowColor: "blue",
    markerTooltipRenderer: (marker) => `${marker.city} (${marker.value})`,
  };

  const [globe, setGlobe] = useState(null);

  return (
    <>
      <section className="aboutHome bg-gray-100 py-20">
        <div className="container mx-auto flex justify-between items-center">
          <div className="left flex-1">
            {globe ? (
              <ReactGlobe
                height="100%"
                width="100%"
                initialCoordinates={[1.3521, 103.8198]}
                markers={markers}
                options={options}
                onClickMarker={(marker, markerObject, event) =>
                  console.log(marker, markerObject, event)
                }
                onGetGlobe={setGlobe}
                onMouseOutMarker={(marker, markerObject, event) =>
                  console.log(marker, markerObject, event)
                }
                onGlobeTextureLoaded={() => console.log("globe loaded")}
                onMouseOverMarker={(marker, markerObject, event) =>
                  console.log(marker, markerObject, event)
                }
              />
            ) : null}
          </div>
          <div className="right flex-1 ml-12">
            <Heading
              subtitle="KEEP LEARNING"
              title="Benefits About AppTechTell Online Learning."
            />
            <div className="items mt-8">
              {homeAbout.map((val) => (
                <Link to={val.link} key={val.title}>
                  <div className="item bg-white rounded-2xl shadow-md hover:shadow-lg p-6 mb-4">
                    <div className="flex items-center mb-4">
                      <div className="img mr-4">
                        <img
                          className="w-16 h-16 rounded-full"
                          src={val.cover}
                          alt=""
                        />
                      </div>
                      <div className="text">
                        <h2 className="text-lg font-semibold">{val.title}</h2>
                        <p className="text-gray-600">{val.desc}</p>
                      </div>
                    </div>
                  </div>
                </Link>
              ))}
            </div>
          </div>
        </div>
      </section>
      <Awrapper />
    </>
  );
};

export default AboutCard;
